`,(0,t.jsxDEV)(n.pre,{children:(0,t.jsxDEV)(n.code,{className:"language-jsx",children:`export const handler = async (req, res) => {
  if (req.method === 'POST' || req.method === 'OPTIONS') {
    const email = validateEmail(req.body.email);
    if (email) {
      const user = getUserByEmail(email);
      if (user) {
        return res.status(200).json({ user });
      } else {
        return res.status(404).json({ message: 'No user found' });
      }
    } else {
      return res.status(422).json({ message: 'Missing email' });
    }
  } else {
    return res.status(405).json({ message: 'Unsupported message' });
  }
};
`},void 0,!1,{fileName:"/Users/evg/Documents/velolider/velolider/content/posts/_mdx_bundler_entry_point-f29d060a-8ead-4370-ba80-56dcf36c05a9.mdx",lineNumber:36,columnNumber:1},this)},void 0,!1,{fileName:"/Users/evg/Documents/velolider/velolider/content/posts/_mdx_bundler_entry_point-f29d060a-8ead-4370-ba80-56dcf36c05a9.mdx",lineNumber:36,columnNumber:1},this),`
