`,(0,t.jsxDEV)(n.pre,{children:(0,t.jsxDEV)(n.code,{className:"language-jsx",children:`const groupBy = (arr, groupFn) => {
  const grouped = {};
  for (const obj of arr) {
    const groupName = groupFn(obj);
    if (!grouped[groupName]) {
      grouped[groupName] = [];
    }
    grouped[groupName].push(obj);
  }
  return grouped;
};
`},void 0,!1,{fileName:"/Users/evg/Documents/velolider/velolider/content/posts/_mdx_bundler_entry_point-f29d060a-8ead-4370-ba80-56dcf36c05a9.mdx",lineNumber:120,columnNumber:1},this)},void 0,!1,{fileName:"/Users/evg/Documents/velolider/velolider/content/posts/_mdx_bundler_entry_point-f29d060a-8ead-4370-ba80-56dcf36c05a9.mdx",lineNumber:120,columnNumber:1},this),`
