`,(0,t.jsxDEV)(n.pre,{children:(0,t.jsxDEV)(n.code,{className:"language-jsx",children:`import React, { useState, useEffect } from 'react';

function FriendListItem(props) {
  const [isOnline, setIsOnline] = useState(null);

  useEffect(() => {
    function handleStatusChange(status) {
      setIsOnline(status.isOnline);
    }

    ChatAPI.subscribeToFriendStatus(props.friend.id, handleStatusChange);
    return () => {
      ChatAPI.unsubscribeFromFriendStatus(props.friend.id, handleStatusChange);
    };
  });

  return (
    <li style={{ color: isOnline ? 'green' : 'black' }}>{props.friend.name}</li>
  );
}
`},void 0,!1,{fileName:"/Users/evg/Documents/velolider/velolider/content/posts/_mdx_bundler_entry_point-f29d060a-8ead-4370-ba80-56dcf36c05a9.mdx",lineNumber:144,columnNumber:1},this)},void 0,!1,{fileName:"/Users/evg/Documents/velolider/velolider/content/posts/_mdx_bundler_entry_point-f29d060a-8ead-4370-ba80-56dcf36c05a9.mdx",lineNumber:144,columnNumber:1},this),`
